DROP TABLE IF EXISTS `series_episodes`;

CREATE TABLE `series_episodes` (
  `id` int(11) NOT NULL,
  `Downloaded` tinyint(1) NOT NULL,
  `Subtitles` tinyint(1) NOT NULL,
  `Seen` tinyint(1) NOT NULL,
  `EpisodeName` text COLLATE latin1_general_ci NOT NULL,
  `EpisodeNumber` int(11) NOT NULL,
  `SeasonNumber` int(11) NOT NULL,
  `FirstAired` text COLLATE latin1_general_ci NOT NULL,
  `SerieID` int(11) NOT NULL,
  `Overview` text COLLATE latin1_general_ci NOT NULL,
  `Rating` float NOT NULL,
  `RatingCount` int(11) NOT NULL,
  `MarkedLone` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci;




DROP TABLE IF EXISTS `series_series`;

CREATE TABLE `series_series` (
  `id` int(11) NOT NULL,
  `SeriesName` text COLLATE latin1_general_ci NOT NULL,
  `Actors` text COLLATE latin1_general_ci NOT NULL,
  `ContentRating` text COLLATE latin1_general_ci NOT NULL,
  `FirstAired` text COLLATE latin1_general_ci NOT NULL,
  `Genre` text COLLATE latin1_general_ci NOT NULL,
  `IMDB_ID` text COLLATE latin1_general_ci NOT NULL,
  `Network` text COLLATE latin1_general_ci NOT NULL,
  `Overview` text COLLATE latin1_general_ci NOT NULL,
  `Rating` float NOT NULL,
  `RatingCount` int(11) NOT NULL,
  `Runtime` int(11) NOT NULL,
  `Status` text COLLATE latin1_general_ci NOT NULL,
  `banner` text COLLATE latin1_general_ci NOT NULL,
  `fanart` text COLLATE latin1_general_ci NOT NULL,
  `lastupdated` text COLLATE latin1_general_ci NOT NULL,
  `poster` text COLLATE latin1_general_ci NOT NULL,
  `Ignore` tinyint(1) NOT NULL,
  `Seen` tinyint(1) NOT NULL DEFAULT '0',
  `userID` int(11) NOT NULL DEFAULT '4',
  `favorite` tinyint(1) NOT NULL DEFAULT '0',
  `last_dbupdate` text COLLATE latin1_general_ci NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci;




DROP TABLE IF EXISTS `series_users`;

CREATE TABLE `series_users` (
  `ID` int(11) NOT NULL AUTO_INCREMENT,
  `name` text,
  PRIMARY KEY (`ID`)
) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=latin1;




